import React, { Component } from "react";
import axios from "axios";
import moment from "moment";

export default class DisplayOrders extends Component {
  nextPage = () => {
    this.props.history.push("/Customerlist");
  };

  componentDidMount() {
    this.getData();
  }
  async getData() {
    await axios
      .get("https://my.api.mockaroo.com/orders.json?key=e49e6840")
      .then((response) => {
        this.setState({ listData1: response.data });
        console.log(">>>>>>>>>>>>>", this.state.listData1);
      });
  }

  handleSubmit = (event) => {
    event.preventDefault();

    this.state.customerobj = {
      name: this.state.name,
      shopname: this.state.shopname,
      status: this.state.status,
    };

    this.setState({ customerobj: this.state.customerobj });
    this.state.name = "";
    this.state.shopname = "";
    this.state.status = "";

    this.postData();

    console.log(this.state.customerobj);
  };
  getDetails(item) {
    this.props.history.push({
      pathname: "/OrderDetails",
      state: item,
    });
  }
  render() {
    return (
      <div>
        <div style={{ textAlign: "center", fontWeight: "bold", fontSize: 18 }}>
          <label>Package Details</label>
        </div>
        <table style={{ width: "100%" }}>
          <tr>
            <th>Serial Number</th>
            <th>Sender Name</th>
            <th>Arraived date</th>
            <th>View details </th>
          </tr>
          {this.state.listData1 !== undefined && this.state.listData1.length > 0
            ? this.state.listData1.map((item, index) => (
                <tr>
                  <td>{index}</td>
                  <td>{item.sender}</td>
                  <td>{moment(item.last_updated).format("MM/DD/YYYY")}</td>
                  <td>
                    <button onClick={() => this.getDetails(item)}>
                      {" "}
                      View Details
                    </button>
                  </td>
                </tr>
              ))
            : []}
        </table>
      </div>
    );
  }
}
